import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import { Table } from 'react-bootstrap';

import {
  searchTransaction
} from './../redux/actions/actions';

class AddressDetail extends Component {
  constructor(props) {
    super(props);
  }

  handleClick(transaction) {
    this.props.searchTransaction(transaction);
  }

    render() {
        //address.transactions is different than address.txrefs, it only includes unique transaction hashes
        console.log(this.props);
        const transactionList = this.props.address.transactions.map((transaction) => {
          return(
            <div key={transaction}>
              <Link to="/transaction" onClick={(e) => {
                e.preventDefault();
                this.handleClick(transaction);
              }}> {transaction} </Link>
            </div>
          );
        });

        return (
            <Table striped bordered hover reponsive>
              <tbody>
                <tr>
                </tr>
                <tr>
                  Wallet Address: {this.props.address.address}
                </tr>
                <tr>
                  Balance: {this.props.address.balance}
                </tr>
                {transactionList}
              </tbody>
            </Table>
        );
    }
}

const mapStateToProps = state => {
    //console.log(state);
    return {
        address: state.search.address,
    }
}

export default connect(mapStateToProps, {searchTransaction})(AddressDetail);
