import React, { Component } from "react";
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';

import {
    login,
    toggleClose,
 } from './../redux/actions/actions';

import '../assets/login.css';

class Login extends Component {

  constructor(props){
    super(props);

    this.state = {
        username: "",
        password: ""
    }

    this.handleClick = (e) => {
      e.preventDefault();
      console.log("handling...");
      this.props.login(this.state.username, this.state.password);
      this.props.toggleClose();
    };

    this.handleChange = (e) => {
          this.setState(
              {
                  [e.target.name]: e.target.value
              }
          );
          console.log(this.state);
    };

  }

  render() {
    return (
      <div>
        <div data-behavior="overlay" className={this.props.modalMode === true ? 'overlay overlay-hugeinc open' : 'overlay overlay-hugeinc'}>
        <button onClick={this.props.toggleClose} data-behavior="close-overlay" type="button" className="overlay-close"><span className="glyphicon glyphicon-remove"></span></button>
        <nav>
          <h2 className="grayed-heading center">Sign In</h2>
          <ul className="omniauth-button-group">
            <li className="omniauth-button google">
              <div className="button google">
                <form className="box-form">
                      <input
                          className="form-item"
                          placeholder="Username"
                          name="username"
                          type="text"
                          onChange={this.handleChange}
                      />
                      <input
                          className="form-item"
                          placeholder="Password"
                          name="password"
                          type="password"
                          onChange={this.handleChange}
                      />
                      <button className="form-submit" onClick={this.handleFormSubmit}>Login</button>
                  </form>
                  <Link className="link" to="/signup">Don't have an account? <span className="link-signup">Signup</span></Link>
              </div>
            </li>
          </ul>
        </nav>
        </div>
    </div>
    );
  }
}

const mapStateToProps = state => {
    return {
        modalMode: state.common.modalMode
    };
}

export default connect(mapStateToProps, {
    toggleClose,
    login
})(Login);
