const initialState = {
    user: {},
    isAuth: false,
}

export default (state = initialState, action) => {
  switch (action.type) {
    case 'LOG_IN':
      console.log('User logging in...');
      console.log(action.user.username);
      return {
        ...state,
        isAuth: Object.keys(action.user).length > 0 ? true : false,
        user: action.user
      }
    case 'LOG_OUT':
      console.log('User logging out...');
      console.log(action.user.username);
      return {
        ...state,
        isAuth: false,
        user: {}
      }
    default:
      return state;
  }
}
