import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Container, Table } from 'react-bootstrap';

class TransactionDetail extends Component {
    render() {
      const payerList = this.props.transaction.payers.map((payer, index) => {
        return(
          <tr key={"payer_" + index}>
            <td>
              {index + 1}
            </td>
            <td>
              {payer.address}
            </td>
            <td>
              -{payer.amount}
            </td>
          </tr>
        );
      });

      const payeeList = this.props.transaction.payees.map((payee, index) => {
        return(
          <tr key={"payee_" + index}>
            <td>
              {index + 1}
            </td>
            <td>
              {payee.address}
            </td>
            <td>
              +{payee.amount}
            </td>
          </tr>
        );
      });

        return (
          <Container className="justify-content-md-center">
            <Table  striped bordered hover>
              <th></th>
              <tr>
                {this.props.transaction.comformed !== null? "Comfirmed at " +  this.props.transaction.comfirmed : "Uncomfirmed"}
              </tr>
              <tr>
                Total amount: {this.props.transaction.total}
              </tr>
              <tr>
                Transaction fees: {this.props.transaction.fees}
              </tr>
            </Table>
            <br/>
            <Table  striped bordered hover>
              <caption>Payer</caption>
              <thead>
                <td>#</td>
                <td>Wallet Address</td>
                <td>Amount</td>
              </thead>
              <tbody>
                {payerList}
              </tbody>
            </Table>
            <br/>
            <Table  striped bordered hover>
              <caption>Payee</caption>
              <thead>
                <td>#</td>
                <td>Wallet Address</td>
                <td>Amount</td>
              </thead>
              <tbody>
                {payeeList}
              </tbody>
            </Table>
          </Container>
        );
    }
}

const mapStateToProps = state => {
  console.log(state);
    return {
        transaction: state.search.transaction,
    }
}

export default connect(mapStateToProps, null)(TransactionDetail);
