import React, { Component } from "react";
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';

import {
    login,
    toggleClose,
 } from './../redux/actions/actions';

import '../assets/login.css';

class Login extends Component {

  constructor(props){
    super(props);

    this.state = {
        username: "",
        password: ""
    }

    this.handleClick = (e) => {
      e.preventDefault();
      this.props.login(this.state.username, this.state.password);
      this.props.toggleClose();
    };

    this.handleChange = (e) => {
          this.setState(
              {
                  [e.target.name]: e.target.value
              }
          );
          //console.log(this.state);
    };

  }

  render() {
    return (
<div class="modal fade" id="modalLoginForm" tabindex="-1" role="dialog" aria-labelledby="myModalLabel" aria-hidden={this.props.modalMode}>
  <div class="modal-dialog" role="document">
    <div class="modal-content">
      <div class="modal-header text-center">
        <h4 class="modal-title w-100 font-weight-bold">Sign in</h4>
        <button type="button" class="close" data-dismiss="modal" aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div class="modal-body mx-3">
        <div class="md-form mb-5">
          <i class="fas fa-envelope prefix grey-text"></i>
          <input type="email" id="defaultForm-email" class="form-control validate" />
          <label data-error="wrong" data-success="right" for="defaultForm-email">Your email</label>
        </div>

        <div class="md-form mb-4">
          <i class="fas fa-lock prefix grey-text"></i>
          <input type="password" id="defaultForm-pass" class="form-control validate" />
          <label data-error="wrong" data-success="right" for="defaultForm-pass">Your password</label>
        </div>

      </div>
      <div class="modal-footer d-flex justify-content-center">
        <button class="btn btn-default">Login</button>
      </div>
    </div>
  </div>
</div>


    //   <div>
    //     <div data-behavior="overlay" className={this.props.modalMode === true ? 'overlay overlay-hugeinc open' : 'overlay overlay-hugeinc'}>
    //       <button onClick={this.props.toggleClose} data-behavior="close-overlay" type="button" className="overlay-close"><span className="glyphicon glyphicon-remove">X</span></button>
    //       <nav className="users-overlay">
    //         <h2 className="grayed-heading center">Log In</h2>
    //         <ul>
    //           <li>
    //             <div>
    //               <form className="box-form">
    //                   <input
    //                       className="form-item"
    //                       placeholder="Username"
    //                       name="username"
    //                       type="text"
    //                       onChange={this.handleChange}
    //                   />
    //                   <input
    //                       className="form-item"
    //                       placeholder="Password"
    //                       name="password"
    //                       type="password"
    //                       onChange={this.handleChange}
    //                   />
    //                   <button className="form-submit" onClick={this.handleClick}>Login</button>
    //               </form>
    //               <Link className="link" to="/signup">Don't have an account? <span className="link-signup">Sign up</span></Link>
    //             </div>
    //           </li>
    //         </ul>
    //       </nav>
    //     </div>
    // </div>
    );
  }
}

const mapStateToProps = state => {
    return {
        modalMode: state.common.modalMode
    };
}

export default connect(mapStateToProps, {
    toggleClose,
    login
})(Login);
