import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom'
import {
  choosePayer,
  deleteWallet,
  addWallet
} from './../redux/actions/actions';

class Wallet extends Component {
  constructor(props) {
    super(props);
    this.state = {
      walletAddressInput: "",
      walletPublicKeyInput: "",
      walletPrivateKeyInput: "",
    };
    this.handleAddClick = this.handleAddClick.bind(this);
    this.handleChooseClick = this.handleChooseClick.bind(this);
    this.handleDeleteClick = this.handleDeleteClick.bind(this);

    this.handleChange = (e) => {
          this.setState(
              {
                  [e.target.name]: e.target.value
              }
          );
    };
  }

  handleChooseClick(index){
    this.props.choosePayer(index);
  }

  handleDeleteClick(walletId){
    this.props.deleteWallet(walletId);
  }

  handleAddClick(){
    this.props.addWallet(this.state);
  }

  // validate the form of inputs
  validate() {
    return {
      walletAddressInput: /^[A-Za-z0-9]+$/.test(this.state.payerAddressInput) && this.state.payerAddressInput.length >= 26 && this.state.payerAddressInput.length <= 35,
      walletPublicKeyInput: /^[a-f0-9]+$/.test(this.state.payerPublicKeyInput) && this.state.payerPublicKeyInput.length === 66,
      walletPrivateKeyInput: /^[A-Za-z0-9]+$/.test(this.state.payerPrivateKeyInput) && this.state.payerPrivateKeyInput.length === 52,
    };
  }

    render() {
        let walletList = this.props.user.wallets.map((wallet, index) => {
          return(
            <div key={"wallet_" + wallet.id}>
              <div>
                Wallet {index} <br />
              </div>
              <div>
                Address: {wallet.address} <br />
              </div>
              <div>
                <button onClick={this.handleChooseClick(index)}> Choose </button>
              </div>
              <div>
                <button onClick={this.handleDeleteClick(wallet.id)}> Delete </button>
              </div>
            </div>
          );
        });

        let validation = this.validate();
        let isDisabled = false;
        let errorMsg = "";
        Object.keys(validation).forEach(
          (key) => {
            if (!validation[key]){
              isDisabled = true;
              errorMsg += "Please check the " + key + " field.\n"
            }
          }
        );

        return (
            <div>
              <div>
                Welcome, {this.props.user.username}! These are your wallets.
              </div>
              {walletList}
              <div>
                <div>
                  New wallet
                </div>
                <form>
                  <div> {errorMsg} </div>
                  <div>
                    Wallet address: <input type="text" name="walletAddressInput" onChange={this.handleChange} placeholder = "Enter your wallet address" /> <br/>
                    Public Key: <input type="text" name="walletPublicKeyInput" onChange={this.handleChange} placeholder = "Enter your wallet public key" / > <br/>
                    WIF: <input type="text" name="walletPrivateKeyInput" onChange={this.handleChange} placeholder = "Enter your wallet private key in Base58 Wallet Import Format" / > <br/>
                  </div>
                  <div>
                    <button type = "button" disabled={isDisabled} onClick={this.handleAddClick}> Add a new wallet </button>
                  </div>
                </form>
              </div>
            </div>
        );
    }
}

const mapStateToProps = state => {
    //console.log(state);
    return {
        user: state.auth.user,
    }
}

export default connect(mapStateToProps, {
  choosePayer,
  deleteWallet
})(Wallet);
