import React, { Component } from 'react';
import { connect } from 'react-redux';
import {
  pay
} from './../redux/actions/actions';

class Payment extends Component {
  constructor(props) {
    super(props);
    this.state = {
      payerAddressInput: this.props.payer.address,
      payeeAddressInput: "",
      payerPublicKeyInput: this.props.payer.publickey,
      payerPrivateKeyInput: this.props.payer.privatekey,
      payingAmountInput: ""
    };
    this.payerAddressInput = React.createRef();
    this.payeeAddressInput = React.createRef();
    this.payerPublicKeyInput = React.createRef();
    this.payerPrivateKeyInput = React.createRef();
    this.payingAmountInput = React.createRef();
    this.handleClick = this.handleClick.bind(this);
  }

  // validate the form of inputs
  validate() {
    return {
      payerAddressInput: /^[A-Za-z0-9]+$/.test(this.state.payerAddressInput) && this.state.payerAddressInput.length >= 26 && this.state.payerAddressInput.length <= 35,
      payeeAddressInput: /^[A-Za-z0-9]+$/.test(this.state.payeeAddressInput) && this.state.payeeAddressInput.length >= 26 && this.state.payeeAddressInput.length <= 35,
      payerPublicKeyInput: /^[a-f0-9]+$/.test(this.state.payerPublicKeyInput) && this.state.payerPublicKeyInput.length === 66,
      payerPrivateKeyInput: /^[A-Za-z0-9]+$/.test(this.state.payerPrivateKeyInput) && this.state.payerPrivateKeyInput.length === 52,
      payingAmountInput: /^[0-9]+$/.test(this.state.payingAmountInput)
    };
  }

  handlePayerAddressInput = e => {
    this.setState({ payerAddressInput: e.target.value });
  };

  handlePayeeAddressInput = e => {
    this.setState({ payeeAddressInput: e.target.value });
  };

  handlePayerPublicKeyInput = e => {
    this.setState({ payerPublicKeyInput: e.target.value });
  };

  handlePayerPrivateKeyInput = e => {
    this.setState({ payerPrivateKeyInput: e.target.value });
  };

  handlePayingAmountInput = e => {
    this.setState({ payingAmountInput: e.target.value });
  };


  handleClick(e) {
    e.preventDefault();
    this.props.pay(this.state);
  }

  render() {
    const validation = this.validate();
    let isDisabled = false;
    let errorMsg = "";
    Object.keys(validation).forEach(
      (key) => {
        if (!validation[key]){
          isDisabled = true;
          errorMsg += "Please check the " + key + " field.\n"
        }
      }
    );

    return (
      <div>
        <form>
          <div> {errorMsg} </div>
          <div>
            From Address <input type="text" value={this.state.payerAddressInput} ref={this.payerAddressInput} onChange={this.handlePayerAddressInput} placeholder = "Enter your paying address" / > to Address <input type="text" ref={this.payeeAddressInput} onChange={this.handlePayeeAddressInput} placeholder = "Enter the payee address" / > <br/>
            Public Key: <input type="text" value={this.state.payerPublicKeyInput} ref={this.payerPublicKeyInput} onChange={this.handlePayerPublicKeyInput} placeholder = "Enter your public key" / > <br/>
            WIF: <input type="text" value={this.state.payerPrivateKeyInput} ref={this.payerPrivateKeyInput} onChange={this.handlePayerPrivateKeyInput} placeholder = "Enter your private key in Base58 Wallet Import Format" / > <br/>
            Amount(Satoshis): <input type="text" ref={this.payingAmountInput} onChange={this.handlePayingAmountInput} placeholder = "Enter the paying amount in Satoshis" / > <br/>
          </div>
          <div>
            <button type = "button" disabled={isDisabled} onClick={this.handleClick}> Submit </button>
          </div>
        </form>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    payer: state.auth.user.payer
  }
}

export default connect(mapStateToProps, {pay})(Payment);
