import React, { Component } from 'react';
import { connect } from 'react-redux';

class TransactionDetail extends Component {
    render() {
      const payerList = this.props.transaction.payers.map((payer, index) => {
        return(
          <div key={"payer_" + index}>
            <div>
              Payer {index}
            </div>
            <div>
              Address: {payer.address}
            </div>
            <div>
              Amount: {payer.amount}
            </div>
          </div>
        );
      });

      const payeeList = this.props.transaction.payees.map((payee, index) => {
        return(
          <div key={"payee_" + index}>
            <div>
              Payee {index}
            </div>
            <div>
              Address: {payee.address}
            </div>
            <div>
              Amount: {payee.amount}
            </div>
          </div>
        );
      });

        return (
            <div>
              <div>
                {this.props.transaction.comformed !== null? "Comfirmed at " +  this.props.transaction.comfirmed : "Uncomfirmed"}
              </div>
              <div>
                {this.props.transaction.doubleSpend? "It is doulbe spent! " : null}
              </div>
              <div>
                Total amount: {this.props.transaction.total}
              </div>
              <div>
                Transaction fees: {this.props.transaction.fees}
              </div>
              <div>
                Payer: <br />
                {payerList}
              </div>
              <div>
                Payee: <br />
                {payeeList}
              </div>
            </div>
        );
    }
}

const mapStateToProps = state => {
  console.log(state);
    return {
        transaction: state.search.transaction,
    }
}

export default connect(mapStateToProps, null)(TransactionDetail);
